vector generatePermutations(string str) {
 vector result;
 sort(str.begin(), str.end());
 do {
 result.push_back(str);
 } while (next_permutation(str.begin(), str.end()));
 return result;
}
int main() {
 // Get input sentence from user
 string sentence;
 cout << "Enter a sentence: ";
 getline(cin, sentence);
 // Remove spaces and punctuation from the sentence
 string cleanedSentence;
 for (char c : sentence) {
 if (isalpha(c)) {
 cleanedSentence += tolower(c);
 }
 }
 // Generate all permutations of the letters in the sentence
 vector permutations = generatePermutations(cleanedSentence);
 // Check each permutation to see if it forms a valid sentence
 for (string permutation : permutations) {
 string newSentence = "";
 int i = 0;
 for (char c : sentence) {
 if (isalpha(c)) {
 newSentence += permutation[i++];
 } else {
 newSentence += c;
 }
 }
 if (isValidSentence(newSentence)) {
 cout << newSentence << endl;
 }
 }
 return 0;
}
