using namespace std;
struct Country {
 string name;
 int count;
};
bool compareCountries(Country c1, Country c2) {
 return c1.count > c2.count;
}
int main() {
 unordered_map sites;
 ifstream file("unesco_sites.txt");
 string line;
 while (getline(file, line)) {
 sites[line]++;
 }
 file.close();

 vector countries;
 for (auto site : sites) {
 if (site.second >= 50) {
 string country = site.first.substr(site.first.find_last_of("(") + 1, 2);
 bool found = false;
 for (auto& c : countries) {
 if (c.name == country) {
 c.count += site.second;
 found = true;
 break;
 }
 }
 if (!found) {
 countries.push_back({country, site.second});
 }
 }
 }

 sort(countries.begin(), countries.end(), compareCountries);
 int top = min(3, (int)countries.size());
 for (int i = 0; i < top; i++) {
 cout << countries[i].name << " - " << countries[i].count << endl;
 }

 return 0;
}
```
