func update_learning_rate(lr float64, target_lr float64, batch_size int, num_epochs int) {
    if lr > target_lr {
        lr = target_lr * 0.1
    }
    if lr < target_lr / 2 {
        lr = target_lr * 0.5
    }
    return lr
}

