```javascript
function create_invoice(customer_name, items) {
    const invoice_items = items.map(item => ({
        name: item.name,
        price: item.price * item.quantity,
        quantity: item.quantity
    }));

    return {
        customer_name: customer_name,
        items: invoice_items
    };
}
