Sure! Here's a sample Java code that generates 10 unique activity options in Paris that highlight the city's romantic essence:
```java
import java.io.File;
import java.io.FileWriter;
import java.io.IOException;
import java.util.ArrayList;
import java.util.Collections;
import java.util.List;
import org.w3c.dom.Document;
import org.w3c.dom.Element;
import org.w3c.dom.Node;
import org.w3c.dom.NodeList;
import javax.xml.parsers.DocumentBuilder;
import javax.xml.parsers.DocumentBuilderFactory;
import javax.xml.parsers.ParserConfigurationException;
import javax.xml.transform.Transformer;
import javax.xml.transform.TransformerException;
import javax.xml.transform.TransformerFactory;
import javax.xml.transform.dom.DOMSource;
import javax.xml.transform.stream.StreamResult;
public class ParisActivitiesGenerator {
 private static final String FILENAME = "paris_activities.xml";
 private static final String[] CAFES = {"Café de Flore", "Les Deux Magots", "Café des Deux Moulins", "Le Consul
 at"};
 private static final String[] BISTROS = {"Le Comptoir du Relais", "Chez L'Ami Jean", "Le Petit Pontoise", "Le Bistro Paul Bert"};
 private static final String[] NIGHTLIFE = {"Moulin Rouge", "Le Caveau de la Huchette", "Le Baron", "Rex Club"};

 public static void main(String[] args) {
 List activities = new ArrayList<>();
 activities.addAll(generateRandomActivities(CAFES, 3));
 activities.addAll(generateRandomActivities(BISTROS, 3));
 activities.addAll(generateRandomActivities(NIGHTLIFE, 4));
 Collections.shuffle(activities);
 writeToFile(activities);
 }

 private static List generateRandomActivities(String[] options, int count) {
 List activities = new ArrayList<>();
 for (int i = 0; i < count; i++) {
 int index = (int) (Math.random() * options.length);
 activities.add(options[index]);
 }
 return activities;
 }

 private static void writeToFile(List activities) {
 try {
 DocumentBuilderFactory docFactory = DocumentBuilderFactory.newInstance();
 DocumentBuilder docBuilder = docFactory.newDocumentBuilder();
 Document doc = docBuilder.newDocument();

 Element rootElement = doc.createElement("activities");
 doc.appendChild(rootElement);

 for (String activity : activities) {
 Element actElement = doc.createElement("activity");
 actElement.appendChild(doc.createTextNode(activity));
 rootElement.appendChild(actElement);
 }

 TransformerFactory transformerFactory = TransformerFactory.newInstance();
 Transformer transformer = transformerFactory.newTransformer();
 DOMSource source = new DOMSource(doc);
 StreamResult result = new StreamResult(new File(FILENAME));
 transformer.transform(source, result);
 } catch (ParserConfigurationException | TransformerException e) {
 e.printStackTrace();
 }
 }
}

