function rollDie() {
 var canvas = document.getElementById("dieCanvas");
 var ctx = canvas.getContext("2d");
 ctx.clearRect(0, 0, canvas.width, canvas.height);
 var num = Math.floor(Math.random() * 6) + 1;
 drawDie(num, ctx);
}
function drawDie(num, ctx) {
 ctx.beginPath();
 ctx.rect(10, 10, 80, 80);
 ctx.fillStyle = "white";
 ctx.fill();
 ctx.strokeStyle = "black";
 ctx.stroke();
 if (num == 1 || num == 3 || num == 5) {
 ctx.beginPath();
 ctx.arc(50, 50, 10, 0, 2 * Math.PI);
 ctx.fillStyle = "black";
 ctx.fill();
 }
 if (num == 2 || num == 3) {
 ctx.beginPath();
 ctx.arc(30, 30, 10, 0, 2 * Math.PI);
 ctx.arc(70, 70, 10, 0, 2 * Math.PI);
 ctx.fillStyle = "black";
 ctx.fill();
 }
 if (num == 4 || num == 5 || num == 6) {
 ctx.beginPath();
 ctx.arc(30, 30, 10, 0, 2 * Math.PI);
 ctx.arc(70, 30, 10, 0, 2 * Math.PI);
 ctx.arc(30, 70, 10, 0, 2 * Math.PI);
 ctx.arc(70, 70, 10, 0, 2 * Math.PI);
 ctx.fillStyle = "black";
 ctx.fill();
 }
 if (num == 6) {
 ctx.beginPath();
 ctx.arc(30, 50, 10, 0, 2 * Math.PI);
 ctx.arc(70, 50, 10, 0, 2 * Math.PI);
 ctx.fillStyle = "black";
 ctx.fill();
 }
}
```
