let sentence = "The quick brown fox jumps over the lazy dog and runs away from the sly cat"
var xmlString = "`n"
var newSentence = "n"
var usedLetters = Set()
let words = sentence.components(separatedBy: " ")
for word in words {
 let firstLetter = String(word.prefix(1))
 if !usedLetters.contains(firstLetter) {
 usedLetters.insert(firstLetter)
 xmlString += " (firstLetter)n"
 newSentence += " (word)n"
 }
}
xmlString += "`"
newSentence += ""
print(xmlString)
print(newSentence)
```
